import React, { useState, useEffect } from 'react';
import Razorpay from 'razorpay';

const App = () => {
  const [orderId, setOrderId] = useState(null);
  const [customerId, setCustomerId] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [amount, setAmount] = useState("");

  const initializeRazorpay = async () => {
    const instance = new Razorpay({
      key_id: 'YOUR_KEY_ID',
      key_secret: 'YOUR_SECRET'
    });

    try {
      // Step 1: Create an order
      const orderData = {
        amount: amount * 100,
        currency: "INR",
        receipt: "receipt#1",
        notes: {
          key1: "value3",
          key2: "value2"
        }
      };

      const order = await instance.orders.create(orderData);
      setOrderId(order.id);

      // Step 2: Create a customer
      const customerData = {
        name: name,
        contact: mobile,
        email: email,
        fail_existing: 0,
        notes: {
          notes_key_1: "Note 1",
          notes_key_2: "Note 2"
        }
      };

      const customer = await instance.customers.create(customerData);
      setCustomerId(customer.id);

      // Step 3: Perform payment with order ID and customer ID
      const paymentOptions = {
        key: 'YOUR_KEY_ID',
        order_id: order.id,
        amount: amount * 100,
        currency: 'INR',
        name: 'Payment Integration',
        description: 'For shopping',
        handler: function (response) {
          alert(response.razorpay_payment_id);
        },
        prefill: {
          name: name,
          email: email,
          contact: mobile
        },
        notes: {
          address: 'Razorpay Corporate Office'
        },
        theme: {
          color: '#3399cc'
        },
        save: 1
      };

      const payment = new window.Razorpay(paymentOptions);
      payment.open();
    } catch (error) {
      console.error('Razorpay integration failed:', error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    initializeRazorpay();
  };

  return (
    <div>
      <h2>Razor Pay Payment Integration</h2>
      <form>
        <label>Name:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <br />
        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <br />
        <label>Mobile Number:</label>
        <input
          type="tel"
          value={mobile}
          onChange={(e) => setMobile(e.target.value)}
        />
        <br />
        <label>Amount:</label>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
        <br />
        <button
          type="submit"
          onClick={handleSubmit}
        >
          Submit Payment
        </button>
      </form>
    </div>
  );
};

export default YourComponent;
