import React, { useState, useEffect } from 'react';

const RazorpayApiExample = () => {
  const [customerData, setCustomerData] = useState(null);

  const keyId = 'rzp_test_Bv7ynRgcpldwRg';
  const secretKey = '03SPtWNWANi7gLRTIPPjX4T3';

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.razorpay.com/v1/customers', {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Basic ${btoa(`${keyId}:${secretKey}`)}`
          }
        });

        if (response.ok) {
          const data = await response.json();
          setCustomerData(data);
        } else {
          console.error('Failed to fetch customer data');
        }
      } catch (error) {
        console.error('Error fetching customer data:', error);
      }
    };

    fetchData();
  }, [keyId, secretKey]);

  return (
    <div>
      {customerData ? (
        <div>
          <h2>Customer Information</h2>
          <p>Name: {customerData.name}</p>
          <p>Email: {customerData.email}</p>
          <p>Contact: {customerData.contact}</p>
        </div>
      ) : (
        <p>Loading customer data...</p>
      )}
    </div>
  );
};

export default RazorpayApiExample;
