import { useState } from 'react';
import './style.css';

const RazorpayComponent = () => {
  const [customerId, setCustomerId] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [amount, setAmount] = useState("");

  const handlePayment = async (e) => {
    e.preventDefault();

    if (name === "" || email === "" || mobile === "") {
      alert("Please enter all the required details.");
      return;
    }

    try {
      // Create customer
      const customerResponse = await fetch('https://api.razorpay.com/v1/customers', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer YOUR_RAZORPAY_API_KEY', // Replace with your Razorpay API key
        },
        body: JSON.stringify({
          name: name,
          contact: mobile,
          email: email,
          fail_existing: 0,
        }),
      });

      const customerData = await customerResponse.json();
      setCustomerId(customerData.id);

      // Initiate payment
      const instance = new window.Razorpay({
        key_id: 'rzp_test_Bv7ynRgcpldwRg', // Replace with your Razorpay key_id
        key_secret: '03SPtWNWANi7gLRTIPPjX4T3', // Replace with your Razorpay key_secret
      });

      var options = {
        key: "rzp_test_Bv7ynRgcpldwRg",
        amount: amount * 100,
        currency: "INR",
        name: "Payment Integration",
        description: "For shopping",
        handler: function (response) {
          alert(response.razorpay_payment_id);
        },
        prefill: {
          name: name,
          email: email,
          contact: mobile,
        },
        notes: {
          address: "Razorpay Corporate Office",
        },
        theme: {
          color: "#3399cc",
        },
        customer_id: customerData.id, // Use the generated customer ID for payment
      };

      var pay = new window.Razorpay(options);
      pay.open();

    } catch (error) {
      console.error('Razorpay customer creation or payment initiation failed:', error);
    }
  };

  return (
    <div>
      <div>
        <h2>Razor Pay Payment Integration</h2>
        <form>
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <br />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <br />
          <input
            type="tel"
            placeholder="Mobile Number"
            value={mobile}
            onChange={(e) => setMobile(e.target.value)}
          />
          <br />
          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <br />
          <button
            type="submit"
            onClick={handlePayment}
          >
            Submit Payment
          </button>
        </form>
      </div>
      <div>
        <p>Customer ID: {customerId}</p>
      </div>
    </div>
  );
};

export default RazorpayComponent;
