import { useState } from 'react';
import './App.css';

function App() {
  const [amount, setAmount] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (amount === "") {
      alert("Please enter the amount.");
    } else {
      var options = {
        key: "rzp_test_KhDzcR49ox6p0k",
        amount: amount * 100,
        currency: "INR",
        name: "Payment Integration",
        description: "For shopping",
        handler: function (response) {
          alert(response.razorpay_payment_id);
        },
        prefill: {
          name: "Ravi",
          email: "ravi@gmail.com",
          contact: "1234567891",
        },
        notes: {
          address: "Razorpay Corporate Office",
        },
        theme: {
          color: "#3399cc",
        },
      };
      var pay = new window.Razorpay(options);
      pay.open();
    }
  };

  return (
    <div className="App" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ backgroundColor: '#fff', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', padding: 'px', borderRadius: '8px', width: '300px' }}>
        <h2 style={{ color: '#3399cc', marginBottom: '20px', textAlign: 'center' }}>Razor Pay Payment Integration</h2>
        <form>
          <input
            type="text"
            placeholder="Enter the amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            style={{ padding: '10px', fontSize: '16px', width: '100%', marginBottom: '20px', borderRadius: '4px', border: '1px solid #ccc' }}
          />
          <br />
          <button
            type="submit"
            onClick={handleSubmit}
            style={{
              backgroundColor: '#3399cc',
              color: '#fff',
              padding: '10px 20px',
              fontSize: '16px',
              cursor: 'pointer',
              borderRadius: '4px',
              border: 'none',
            }}
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
