import { useState } from 'react';
import './style.css';

const RazorpayComponent = () => {
  const [customerId, setCustomerId] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [amount, setAmount] = useState("");

  const createRazorpayCustomer = async () => {
    try {
      // Initialize Razorpay instance
      var instance = new window.Razorpay({
        key_id: 'rzp_test_Bv7ynRgcpldwRg', // Replace with your Razorpay key_id
        key_secret: 'YOUR_KEY_SECRET', // Replace with your Razorpay key_secret
      });

      // Create customer using Razorpay instance
      const customer = await instance.customers.create({
        name: name,
        contact: mobile,
        email: email,
        fail_existing: 0,
        notes: {
          notes_key_1: "Tea, Earl Grey, Hot",
          notes_key_2: "Tea, Earl Grey… decaf."
        }
      });

      // Update state with the created customer ID
      setCustomerId(customer.id);

    } catch (error) {
      console.error('Razorpay customer creation failed:', error);
    }
  };

  const handlePayment = (e) => {
    e.preventDefault();
    // Your payment handling logic here
  };

  return (
    <div>
      <div>
        <h2>Razor Pay Payment Integration</h2>
        <form>
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <br />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <br />
          <input
            type="tel"
            placeholder="Mobile Number"
            value={mobile}
            onChange={(e) => setMobile(e.target.value)}
          />
          <br />
          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <br />
          <button
            type="button"
            onClick={createRazorpayCustomer}
          >
            Create Razorpay Customer
          </button>
          <button
            type="submit"
            onClick={handlePayment}
          >
            Submit Payment
          </button>
        </form>
      </div>
      <div>
        <p>Customer ID: {customerId}</p>
      </div>
    </div>
  );
};

export default RazorpayComponent;
