import { useState, useEffect } from 'react';

const RazorpayComponent = () => {
  const [customerId, setCustomerId] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [amount, setAmount] = useState("");

  useEffect(() => {
    const initializeRazorpay = async () => {
      const instance = new window.Razorpay({
        key_id: 'rzp_test_Bv7ynRgcpldwRg',
        key_secret: '',
      });

      try {
        const customerData = {
          name: name,
          contact: mobile,
          email: email,
          fail_existing: 0,
          notes: {
            notes_key_1: "Tea, Earl Grey, Hot",
            notes_key_2: "Tea, Earl Grey… decaf."
          }
        };

        const customer = await instance.customers.create(customerData);

        setCustomerId(customer.id);
      } catch (error) {
        console.error('Razorpay customer creation failed:', error);
      }
    };

    initializeRazorpay();
  }, [name, email, mobile]);

  const handlePayment = (e) => {
    e.preventDefault();
    if (amount === "") {
      alert("Please enter the amount.");
    } else {
      var options = {
        key: "rzp_test_KhDzcR49ox6p0k",
        amount: amount * 100,
        currency: "INR",
        name: "Payment Integration",
        description: "For shopping",
        handler: function (response) {
          alert(response.razorpay_payment_id);
        },
        prefill: {
          name: name,
          email: email,
          contact: mobile,
        },
        notes: {
          address: "Razorpay Corporate Office",
        },
        theme: {
          color: "#3399cc",
        },
        save: 1,  // Indicate to save payment details for future use
      };
      var pay = new window.Razorpay(options);
      pay.open();
    }
  };

  return (
    <div>
      <div>
        <h2>Razor Pay Payment Integration</h2>
        <form>
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <br />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <br />
          <input
            type="tel"
            placeholder="Mobile Number"
            value={mobile}
            onChange={(e) => setMobile(e.target.value)}
          />
          <br />
          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <br />
          <button
            type="submit"
            onClick={handlePayment}
          >
            Submit Payment
          </button>
        </form>
      </div>
      <div>
        <p>Customer ID: {customerId}</p>
      </div>
    </div>
  );
};

export default RazorpayComponent;
