import { useState } from 'react';
import './App.css';

function App() {
  const [amount, setamount] = useState("")

  const handleSubmit = (e) =>
  {
    e.preventDefault();
    if(amount === "" )
    {
      alert("Please enter amount.");
    }
    else{
      var options = {
        key:"rzp_test_KhDzcR49ox6p0k",
        key_secret:"9iCXFV82o3eXTQ3j8J6GLjFN",
        amount: amount*100,
        currency: "INR",
        name:"Payment Integration",
        description : "For shoping",
        handler: function(response){
          alert(response.razorpay_payment_id);
        },
        prefill : {
          name: "Ravi",
          email : "ravi@gmail.com",
          contact :"1234567891"
        },
        notes:{
          address: "Razorpay Corporate Office",
        },
        theme: {
          color: "#3399cc"
        }
      };
      var pay = new window.Razorpay(options);
      pay.open();
    }
  }
  return (
    <div className="App">
        <h2>Razor Pay Payment Integration</h2>
        <br/>
        <input type="text" placeholder='Enter the amount' value ={amount} onChange={(e)=>setamount(e.target.value)}/>
        <br/><br/>
        <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}

export default App;
