import { useState } from 'react';
import './App.css';

function App() {
  const [amount, setAmount] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (amount === "") {
      alert("Please enter the amount.");
    } else {
      var options = {
        key: "rzp_test_KhDzcR49ox6p0k",
        amount: amount * 100,
        currency: "INR",
        name: "Payment Integration",
        description: "For shopping",
        handler: function (response) {
          alert(response.razorpay_payment_id);
        },
        prefill: {
          name: "Ravi",
          email: "ravi@gmail.com",
          contact: "1234567891",
        },
        notes: {
          address: "Razorpay Corporate Office",
        },
        theme: {
          color: "#3399cc",
        },
      };
      var pay = new window.Razorpay(options);
      pay.open();
    }
  };

  return (
    <div className="App" style={{ textAlign: 'center', padding: '20px' }}>
      <div style={{ background: '#fff', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', padding: '20px', maxWidth: '400px', margin: 'auto' }}>
        <h2 style={{ color: '#3399cc' }}>Razor Pay Payment Integration</h2>
        <br />
        <input
          type="text"
          placeholder="Enter the amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          style={{ padding: '10px', fontSize: '16px', marginBottom: '15px', width: '100%' }}
        />
        <br />
        <button
          onClick={handleSubmit}
          style={{
            backgroundColor: '#3399cc',
            color: '#fff',
            padding: '10px 20px',
            fontSize: '16px',
            cursor: 'pointer',
            borderRadius: '5px',
            border: 'none',
          }}
        >
          Submit
        </button>
      </div>
    </div>
  );
}

export default App;
