import React, { useState, useEffect } from 'react';

const RazorpayApiExample = () => {
  const [customerData, setCustomerData] = useState([]);
  const keyId = 'rzp_test_Bv7ynRgcpldwRg';
  const secretKey = '03SPtWNWANi7gLRTIPPjX4T3';

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.razorpay.com/v1/customers', {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Basic ${btoa(`${keyId}:${secretKey}`)}`
          }
        });

        if (response.ok) {
          const data = await response.json();
          setCustomerData(data.items || []); // Assuming the array of customers is inside the 'items' property
        } else {
          console.error('Failed to fetch customer data. Status:', response.status);
          console.error('Response:', await response.text());
        }
      } catch (error) {
        console.error('Error fetching customer data:', error);
      }
    };

    fetchData();
  }, [keyId, secretKey]);

  return (
    <div>
      {customerData.length > 0 ? (
        <div>
          <h2>Customer Information</h2>
          {customerData.map((customer, index) => (
            <div key={index}>
              <p>Name: {customer.name}</p>
              <p>Email: {customer.email}</p>
              <p>Contact: {customer.contact}</p>
              <hr />
            </div>
          ))}
        </div>
      ) : (
        <p>Loading customer data...</p>
      )}
    </div>
  );
};

export default RazorpayApiExample;
