import React, { useState, useEffect } from 'react';
import './App.css';

const RazorpayComponent = () => {
  const [customerId, setCustomerId] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [amount, setAmount] = useState("");

  useEffect(() => {
    const initializeRazorpay = async () => {
      const instance = new window.Razorpay({
        key_id: 'rzp_test_Bv7ynRgcpldwRg',
        key_secret: '03SPtWNWANi7gLRTIPPjX4T3',
      });

      try {
        // Step 1: Create an order
        const orderData = {
          amount: amount * 100,  // Convert amount to paise
          currency: "INR",
          receipt: "receipt#1",
          notes: {
            key1: "value3",
            key2: "value2"
          }
        };

        const order = await instance.orders.create(orderData);

        // Step 2: Create a customer
        const customerData = {
          name: name,
          contact: mobile,
          email: email,
          fail_existing: 0,
          notes: {
            notes_key_1: "Tea, Earl Grey, Hot",
            notes_key_2: "Tea, Earl Grey… decaf."
          }
        };

        const customer = await instance.customers.create(customerData);

        setCustomerId(customer.id);

        // Step 3: Perform payment with order ID and customer ID
        const options = {
          key: "rzp_test_Bv7ynRgcpldwRg",
          order_id: order.id,
          amount: amount * 100,
          currency: "INR",
          name: "Payment Integration",
          description: "For shopping",
          handler: function (response) {
            alert(response.razorpay_payment_id);
          },
          prefill: {
            name: name,
            email: email,
            contact: mobile,
          },
          notes: {
            address: "Razorpay Corporate Office",
          },
          theme: {
            color: "#3399cc",
          },
          save: 1,
        };

        const pay = new window.Razorpay(options);
        pay.open();
      } catch (error) {
        console.error('Razorpay integration failed:', error);
      }
    };

    initializeRazorpay();
  }, [name, email, mobile, amount]);

  return (
    <div className="App">
      <div className="card">
        <h2>Razor Pay Payment Integration</h2>
        <form>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <br />
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <br />
          <label>Mobile Number:</label>
          <input
            type="tel"
            value={mobile}
            onChange={(e) => setMobile(e.target.value)}
          />
          <br />
          <label>Amount:</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <br />
          <button
            type="submit"
            onClick={(e) => e.preventDefault()}
          >
            Submit Payment
          </button>
        </form>
      </div>
      <div>
        <p>Customer ID: {customerId}</p>
      </div>
    </div>
  );
};

export default RazorpayComponent;
