import { useState, useEffect } from 'react';
import './style.css';

const RazorpayComponent = () => {
  const [customerId, setCustomerId] = useState(null);
  const [orderId, setOrderId] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [amount, setAmount] = useState("");

  useEffect(() => {
    const initializeRazorpay = async () => {
      const instance = new window.Razorpay({
        key_id: 'rzp_test_Bv7ynRgcpldwRg',
        key_secret: '03SPtWNWANi7gLRTIPPjX4T3',
      });

      try {
        const customerData = {
          name: name,
          contact: mobile,
          email: email,
          fail_existing: 0,
        };

        const customer = await instance.customers.create(customerData);
        setCustomerId(customer.id);

        const orderData = {
          amount: amount * 100,
          currency: "INR",
          receipt: "receipt#1",
          customer_id: customer.id,
        };

        const order = await instance.orders.create(orderData);
        setOrderId(order.id);

        sessionStorage.setItem('customerId', customer.id);
        sessionStorage.setItem('orderId', order.id);

      } catch (error) {
        console.error('Razorpay customer and order creation failed:', error);
      }
    };

    initializeRazorpay();
  }, [name, amount, email, mobile]);

  useEffect(() => {
    // Fetch customer and order information using IDs from sessionStorage
    const fetchCustomerOrderInfo = async () => {
      const storedCustomerId = sessionStorage.getItem('customerId');
      const storedOrderId = sessionStorage.getItem('orderId');

      if (storedCustomerId && storedOrderId) {
        setCustomerId(storedCustomerId);
        setOrderId(storedOrderId);

        // Optionally, you can fetch additional information here
        // Example: const customerInfo = await fetchCustomerInfo(storedCustomerId);
        // Example: const orderInfo = await fetchOrderInfo(storedOrderId);
      }
    };

    fetchCustomerOrderInfo();
  }, []);

  const handlePayment = (e) => {
    e.preventDefault();
    if (amount === "") {
      alert("Please enter the amount.");
    } else {
      // Your existing payment handling code
    }
  };

  return (
    <div>
      <div>
        <h2>Razor Pay Payment Integration</h2>
        <form>
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <br />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <br />
          <input
            type="tel"
            placeholder="Mobile Number"
            value={mobile}
            onChange={(e) => setMobile(e.target.value)}
          />
          <br />
          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <br />
          <button
            type="submit"
            onClick={handlePayment}
          >
            Submit Payment
          </button>
        </form>
      </div>
      <div>
        <p>Customer ID: {customerId}</p>
        <p>Order ID: {orderId}</p>
      </div>
    </div>
  );
};

export default RazorpayComponent;
