import { useState, useEffect } from 'react';
import './style.css';

const RazorpayComponent = () => {
  const [customerId, setCustomerId] = useState(null);
  const [orderId, setOrderId] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [amount, setAmount] = useState("");

  useEffect(() => {
    const initializeRazorpay = async () => {
      try {
        // Create customer
        const customerData = {
          name: name,
          contact: mobile,
          email: email,
          fail_existing: 0,
        };

        const customerResponse = await fetch("https://api.razorpay.com/v1/customers", {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Basic ' + btoa('rzp_test_Bv7ynRgcpldwRg:03SPtWNWANi7gLRTIPPjX4T3'),
          },
          body: JSON.stringify(customerData),
        });

        const customer = await customerResponse.json();
        setCustomerId(customer.id);

        // Create order
        const orderData = {
          amount: amount * 100,
          currency: "INR",
          receipt: "receipt#1",
          customer_id: customer.id,
        };

        const orderResponse = await fetch("https://api.razorpay.com/v1/orders", {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Basic ' + btoa('rzp_test_Bv7ynRgcpldwRg:03SPtWNWANi7gLRTIPPjX4T3'),
          },
          body: JSON.stringify(orderData),
        });

        const order = await orderResponse.json();

        if (order.id) {
          setOrderId(order.id);
          sessionStorage.setItem('customerId', customer.id);
          sessionStorage.setItem('orderId', order.id);
        } else {
          console.error('Razorpay order creation failed:', order);
        }

      } catch (error) {
        console.error('Razorpay customer creation failed:', error);
      }
    };

    initializeRazorpay();
  }, [name, amount, email, mobile]);

  useEffect(() => {
    // Fetch customer and order information using IDs from sessionStorage
    const fetchCustomerOrderInfo = async () => {
      const storedCustomerId = sessionStorage.getItem('customerId');
      const storedOrderId = sessionStorage.getItem('orderId');

      if (storedCustomerId && storedOrderId) {
        setCustomerId(storedCustomerId);
        setOrderId(storedOrderId);
      }
    };

    fetchCustomerOrderInfo();
  }, []);

  const handlePayment = (e) => {
    e.preventDefault();
    if (amount === "") {
      alert("Please enter the amount.");
    } else {
      var options = {
        key: "rzp_test_Bv7ynRgcpldwRg", // Add your Razorpay key here
        amount: amount * 100,
        currency: "INR",
        name: "Payment Integration",
        description: "For shopping",
        handler: function (response) {
          if (response.razorpay_order_id) {
            // Use response.razorpay_order_id as the order ID
            alert("ORDER ID: " + response.razorpay_order_id);
          } else {
            // Handle the case where order ID is not available
            alert("Order ID is not available");
          }

          // Use response.razorpay_payment_id as the payment ID
          alert("PAYMENT ID: " + response.razorpay_payment_id);
        },
        prefill: {
          name: name,
          email: email,
          contact: mobile,
        },
        notes: {
          address: "Razorpay Corporate Office",
        },
        theme: {
          color: "#3399cc",
        },
        customer_id: customerId,
        order_id: orderId,
        save: 1
      };
      var pay = new window.Razorpay(options);
      pay.open();
    }
  };


  return (
    <div>
      <div>
        <h2>Razor Pay Payment Integration</h2>
        <form>
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <br />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <br />
          <input
            type="tel"
            placeholder="Mobile Number"
            value={mobile}
            onChange={(e) => setMobile(e.target.value)}
          />
          <br />
          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <br />
          <button
            type="submit"
            onClick={handlePayment}
          >
            Submit Payment
          </button>
        </form>
      </div>
      <div>
        <p>Customer ID: {customerId}</p>
        <p>Order ID: {orderId}</p>
      </div>
    </div>
  );
};

export default RazorpayComponent;
