import { useState, useEffect } from 'react';
import './style.css';

const RazorpayComponent = () => {
  const [customerId, setCustomerId] = useState(null);
  const [orderId, setOrderId] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [amount, setAmount] = useState("");

  useEffect(() => {
    const initializeRazorpay = async () => {
      const instance = new window.Razorpay({
        key_id: 'rzp_test_Bv7ynRgcpldwRg',
        key_secret: '03SPtWNWANi7gLRTIPPjX4T3',
      });

      try {
        const customerData = {
          name: name,
          contact: mobile,
          email: email,
          fail_existing: 0,
        };

        const customer = await instance.customers.create(customerData);

        setCustomerId(customer.id);

        const orderData = {
          amount: amount * 100,
          currency: "INR",
          receipt: "receipt#1"
        };

        const order = await instance.orders.create(orderData);

        setOrderId(order.id);

        // Save IDs to sessionStorage
        sessionStorage.setItem('customerId', customer.id);
        sessionStorage.setItem('orderId', order.id);

      } catch (error) {
        console.error('Razorpay customer creation failed:', error);
      }
    };

    initializeRazorpay();
  }, [name, amount, email, mobile]);

  const handlePayment = (e) => {
    e.preventDefault();
    if (amount === "") {
      alert("Please enter the amount.");
    } else {
      var options = {
        key: "rzp_test_Bv7ynRgcpldwRg",
        amount: amount * 100,
        currency: "INR",
        name: "Payment Integration",
        description: "For shopping",
        handler: function (response) {
          alert(response.razorpay_payment_id);
        },
        prefill: {
          name: name,
          email: email,
          contact: mobile,
        },
        notes: {
          address: "Razorpay Corporate Office",
        },
        theme: {
          color: "#3399cc",
        },
        customerId: customerId,
        orderId: orderId,
        save: 1
      };
      var pay = new window.Razorpay(options);
      pay.open();
    }
  };

  // Retrieve IDs from sessionStorage on component mount
  useEffect(() => {
    const storedCustomerId = sessionStorage.getItem('customerId');
    const storedOrderId = sessionStorage.getItem('orderId');

    if (storedCustomerId && storedOrderId) {
      setCustomerId(storedCustomerId);
      setOrderId(storedOrderId);
    }
  }, []);

  return (
    <div>
      <div>
        <h2>Razor Pay Payment Integration</h2>
        <form>
          {/* ... (rest of your form inputs) */}
          <button
            type="submit"
            onClick={handlePayment}
          >
            Submit Payment
          </button>
        </form>
      </div>
      <div>
        <p>Customer ID: {customerId}</p>
        <p>Order ID: {orderId}</p>
      </div>
    </div>
  );
};

export default RazorpayComponent;
